        [TestMethod]
        public void MultithreadingParse()
        {
            var threads = new List<Thread>();
            var exceptions = new ConcurrentBag<Exception>();
            for (var i = 0; i < 200; i++)
            {
                Thread t = new Thread(ThreadMethod(exceptions));
                t.Start();
                threads.Add(t);
            }

            foreach (var t in threads)
            {
                t.Join();
            }

            if (exceptions.Any())
            {
                exceptions.TryPeek(out var e);
                ExceptionDispatchInfo.Capture(e).Throw();
            }
        }

        private ThreadStart ThreadMethod(ConcurrentBag<Exception> exceptions)
        {
            return () =>
            {
                try
                {
                    var query = "Name == \"FailedWorkflows\" and Source == \"Kusto_CDOC\"";
                    var where = new WhereOperator(query);
                }
                catch (Exception e)
                {
                    exceptions.Add(e);
                }
            };
        }

