        [TestMethod]
        [TestCategory("RxKQL")]
        [Owner("Dan Nicolescu")]
        public void TestExpandObjectWithList()
        {
            dynamic testObject = new ExpandoObject();
            testObject.Rows = new List<Object>();

            dynamic row = new ExpandoObject();
            testObject.Rows.Add(row);
            var guid = Guid.NewGuid();
            row.SubscriptionId = guid;

            ExtendOperator extendOperator = new ExtendOperator("SubscriptionId = Rows[0].SubscriptionId");
            dynamic e = extendOperator.Extend(testObject);
            var subscriptionId = (Guid)e.SubscriptionId;
            Assert.AreEqual(subscriptionId, guid);
        }

        [TestMethod]
